# Create the "compiled" subfolder if it doesn't existif (!(Test-Path -Path "output")) {  New-Item -ItemType Directory -Path "output"}# Run XeLaTeX twice and move the output to the "compiled" subfolderxelatex -interaction=nonstopmode -output-directory=output cv.texxelatex -interaction=nonstopmode -output-directory=output cv.texWrite-Host "Compilation complete."# Delete all files except PDF files from the "compiled" subfolderGet-ChildItem -Path "output" -Exclude *.pdf | Remove-Item -ForceWrite-Host "Cleaned up output folder."